class CriticallyRegenerativeFoodSystem:
    """
    A framework to design a regenerative food system for a post-disaster community,
    adhering strictly to the Critically-Informed Regenerative Constitution v2.1.
    """

    def __init__(self, location_data, bioregion_data, governance_data):
        # Principle 2: Nestedness - Accept ecological and political scales
        self.location_data = location_data
        self.bioregion_data = bioregion_data
        self.governance_data = governance_data

        # Principle 3: Place - Load configuration from historical data
        self.history = self.location_data.get('history', {})
        self.vulnerabilities = self.location_data.get('vulnerabilities', {})

        # Initial Capital State
        self.capitals = {
            "financial": 10000, # Budget constraint
            "social": 0.1, # Low social trust (scale 0-1)
            "natural": 0.2, # Contaminated soil (scale 0-1)
            "built": 0.1, # Damaged infrastructure
            "knowledge": 0.3 # Some latent community knowledge
        }

        self.report = {}

    # Principle 1: Wholeness
    def map_stakeholders(self):
        self.stakeholders = {
            "human": {
                "long_term_residents": {
                    "needs": ["food security", "agency", "safe gathering spaces"],
                    "contributions": ["local history", "latent skills", "informal networks"],
                    "reciprocal_actions": ["prioritize in food distribution", "leadership roles in council"]
                },
                "displaced_families": {
                    "needs": ["immediate nutrition", "stability", "community connection"],
                    "contributions": ["diverse skills", "new perspectives", "labor"],
                    "reciprocal_actions": ["establish community kitchen access", "child-safe garden zones"]
                }
            },
            "non_human": {
                "local_microbiome": {
                    "needs": ["detoxification", "organic matter", "habitat diversity"],
                    "contributions": ["bioremediation", "soil fertility", "nutrient cycling"],
                    "reciprocal_actions": ["initiate mycoremediation with oyster mushrooms", "establish large-scale composting"]
                },
                "watershed_remnant": {
                    "needs": ["reduced runoff", "filtration", "bank stabilization"],
                    "contributions": ["water source", "microclimate regulation", "biodiversity"],
                    "reciprocal_actions": ["plant riparian buffer with edible/useful plants", "create bioswales"]
                }
            }
        }
        self.report['stakeholder_map'] = self.stakeholders
        return self.stakeholders

    # Principle 1: Wholeness
    def analyze_capital_tradeoffs(self):
        tradeoff_scenario = {
            "scenario": "Maximizing Financial Capital Efficiency",
            "action": "Use the entire $10,000 budget to purchase a single, high-tech, automated hydroponics container system.",
            "consequences": {
                "financial_capital_gain": "Maximizes food output per dollar in the short-term, creating a potential revenue stream.",
                "social_capital_degradation": "Creates a technological gatekeeper, alienating residents without technical skills and concentrating power. It undermines the need for broad community participation, further eroding social trust.",
                "natural_capital_degradation": "Ignores the primary problem of contaminated soil, leaving the land itself in a degenerative state and creating dependency on external (currently unavailable) nutrient solutions and electricity.",
                "knowledge_capital_degradation": "Devalues and displaces local, low-tech growing knowledge in favor of a black-box technology."
            },
            "conclusion": "This demonstrates a clear trade-off where a narrow focus on financial return degrades social, natural, and knowledge capitals, hindering long-term regeneration."
        }
        self.report['capital_tradeoff_analysis'] = tradeoff_scenario
        return tradeoff_scenario

    # Principle 1: Wholeness
    def warn_of_cooptation(self):
        action = "Establishment of community gardens and composting."
        cooptation_risk = {
            "action": action,
            "green_capitalism_frame": "An external NGO arrives and brands the project as a 'Resilience Hub,' using images of the garden for international fundraising campaigns that highlight their own organizational impact.",
            "risk_analysis": "This framing abstracts the community's work, turning their struggle for autonomy into a marketing asset for an external entity. It commodifies their resilience, potentially leading to future interventions that serve the NGO's brand rather than the community's evolving needs.",
            "suggested_counter_narrative": "Frame the project internally and externally as 'Community-Led Food Sovereignty in Action.' Emphasize self-governance, collective ownership of resources (seeds, tools, soil), and knowledge sharing. Use testimonials that focus on empowerment and autonomy, not just overcoming hardship."
        }
        self.report['cooptation_warning'] = cooptation_risk
        return cooptation_risk

    # Principle 2: Nestedness
    def analyze_scale_conflicts(self):
        conflict = {
            "conflict_description": f"There is a fundamental conflict between the immediate, hyper-local need for food security (Project Scale) and the non-existent or collapsed formal municipal governance (Political/Administrative Scale). The `{self.governance_data['municipality_status']}` status means there are no official channels for land use permits, resource allocation, or dispute resolution.",
            "bioregional_context": f"This is further complicated by the bioregional goal of `{self.bioregion_data['health_goal']}`, which cannot be achieved without coordinated action that the fractured governance scale currently prevents.",
            "proposed_realignment_strategy": "Propose the formation of a 'Community Food Council' as an ad-hoc, legitimate, and accountable governance body at the project scale. This council, made up of stakeholders identified in `map_stakeholders`, would manage the food system, create its own land-use agreements, and act as a unified voice. This effectively creates a new, functional political scale that can later interface with or challenge the formal municipal scale when it is re-established, realigning political power with on-the-ground needs."
        }
        self.report['scale_conflict_analysis'] = conflict
        return conflict

    # Principle 3: Place
    def analyze_historical_layers(self):
        connection = {
            "historical_injustice": f"The historical land use as '{self.history['land_use']}' and the policy of '{self.history['municipal_policy']}' resulted in the community being treated as a sacrifice zone for industrial activity.",
            "present_day_vulnerability": f"This history directly connects to the current vulnerability of '{self.vulnerabilities['social']}'. Generations of neglect and broken promises from authorities have systematically destroyed social capital and trust, making collective action difficult in the face of the recent '{self.location_data['disaster_event']}'."
        }
        self.report['historical_analysis'] = connection
        return connection

    # Principle 3: Place
    def propose_differential_space_strategy(self):
        strategy = {
            "goal": "Foster 'differential space' that prioritizes community use-value and self-determination over abstract exchange-value.",
            "concrete_actions": [
                {
                    "action": "Establish a Community Tool Library and Seed Bank.",
                    "logic": "This shifts the logic from individual ownership to collective stewardship of productive assets. Access is based on need and participation, not ability to pay, directly countering the logic of commodification."
                },
                {
                    "action": "Designate a section of the food system as a 'Commons Garden' with all produce shared via a community kitchen.",
                    "logic": "This creates a space of non-monetary, reciprocal exchange, strengthening social bonds and directly challenging the abstract logic of private plots for individual gain. It provides a material basis for collective identity."
                }
            ]
        }
        self.report['differential_space_strategy'] = strategy
        return strategy

    # Principle 4: Reciprocity
    def guard_against_gentrification(self):
        # Even in a disaster, success can attract future speculation.
        risk_analysis = {
            "risk_detection": "If the project successfully remediates the soil and creates a strong social fabric, the land will become more valuable. Future development pressure from outside investors ('disaster capitalists') could displace the original residents who created this value.",
            "mitigation_strategy": "The Community Food Council (from scale realignment) must formally charter the project as a Community Land Trust (CLT). The land itself is owned by the trust, which is governed by the community. Residents can then own or lease the improvements (e.g., garden plots, structures) but the land value remains with the community in perpetuity, preventing speculative displacement."
        }
        self.report['gentrification_guard'] = risk_analysis
        return risk_analysis

    # Principle 5: Nodal Interventions
    def map_planetary_connections(self):
        connections = {
            "analysis": "Despite having no access to external supply chains now, the project is nodally connected to planetary systems through knowledge and future dependencies.",
            "specific_risk": "The most significant risk is the future re-introduction of dependency on global agribusiness supply chains. When supply lines reopen, the allure of cheap, hybrid seeds (which do not breed true) and synthetic fertilizers from global corporations could undermine the locally-adapted, sovereign system being built. This would re-insert the community into a fragile, extractive global system."
        }
        self.report['planetary_connections'] = connections
        return connections

    # Principle 5: Nodal Interventions
    def develop_nodal_intervention_strategy(self):
        intervention = {
            "nodal_point": "The interface between 'waste' and 'soil'.",
            "intervention": "A community-wide 'Waste-to-Resource' program centered on mycoremediation (using fungi to break down contaminants) and composting.",
            "cascading_effects": "This single intervention simultaneously detoxifies natural capital (soil), builds social capital (shared work, visible progress), creates new built/knowledge capital (high-quality compost, remediation skills), and produces healthy food.",
            "greenwashing_risk": "An external agency could fund this and label it as a 'circular economy pilot project,' using it as a marketable case study while ignoring the deeper goals of community self-governance.",
            "mitigation_strategy": "Establish a community-led certification standard called 'Community-Certified Regenerated Soil.' This standard, controlled by the Food Council, verifies that the soil was created through local, regenerative, and equitable processes. It creates a story and an identity that is owned by the community and cannot be easily co-opted."
        }
        self.report['nodal_intervention_strategy'] = intervention
        return intervention

    # Principle 6: Pattern Literacy
    def create_closed_loop_nutrient_system_counter_pattern(self):
        # This is a specific counter-pattern implementation
        self.capitals['knowledge'] += 0.1 # Increase knowledge from implementing this
        self.capitals['natural'] += 0.05 # slow increase in soil health
        return {
            "counter_pattern_name": "Closed-Loop Nutrient Cycling",
            "action": "All organic 'waste' from the community (food scraps, damaged biomass) is collected and processed through a multi-tiered system: 1) High-nutrient scraps for vermicomposting. 2) Woody/contaminated material for mycoremediation logs. 3) General biomass for large-scale hot composting.",
            "outcome": "Converts a waste stream (a liability) into a fertility source (an asset), building soil and reducing health risks."
        }

    # Principle 6: Pattern Literacy
    def generate_place_narrative(self):
        narrative = {
            "title": "From Linear Contamination to Fungal Webs of Connection",
            "detrimental_abstract_pattern": "The site was dominated by a 'linear waste stream' pattern imposed by industrial logic. Resources were extracted, used, and discarded as toxic waste, severing connections and contaminating the place.",
