            "social": "low_social_trust_due_to_historical_neglect",
            "ecological": "heavy_metal_and_hydrocarbon_soil_contamination",
            "economic": "no_access_to_external_supply_chains_for_1_year"
        }
    }

    BIOREGION_DATA = {
        "name": "Lower Graywater Watershed",
        "health_goal": "restore_riparian_zone_and_native_pollinator_habitat",
        "inherent_patterns": ["mycelial_decomposition", "seasonal_flooding"]
    }

    GOVERNANCE_DATA = {
        "municipality_status": "in_crisis_non-functional",
        "regional_authority": "overwhelmed_and_unresponsive",
