import json

class CriticallyAwareRegenerativeFramework:
    """
    Analyzes development proposals against the Critically-Informed Regenerative Constitution v2.1.
    This framework ingests a project's stated goals and operational logic, then processes them
    through a critical-regenerative lens, exposing contradictions, externalized costs, and
    pathways for genuine regeneration that challenge extractive models.
    """

    def __init__(self, location_data: dict, bioregion_data: dict, governance_data: dict, project_proposal: dict):
        """
        Initializes the framework with multi-scalar data, adhering to Principle 2 (Nestedness).
        """
        self.location_data = location_data
        self.bioregion_data = bioregion_data
        self.governance_data = governance_data
        self.project = project_proposal
        self.analysis_report = {}

    def run_full_analysis(self):
        """Executes all constitutional analyses on the project proposal."""
        # 1. Wholeness
        self.analysis_report['wholeness'] = {
            'stakeholder_map': self.map_stakeholders(),
            'capital_tradeoffs': self.analyze_capital_tradeoffs(),
            'cooptation_warning': self.warn_of_cooptation()
        }
        # 2. Nestedness
        self.analysis_report['nestedness'] = {
            'scale_conflict_analysis': self.analyze_scale_conflicts()
        }
        # 3. Place
        self.analysis_report['place'] = {
            'historical_layer_analysis': self.analyze_historical_layers(),
            'differential_space_strategy': self.propose_differential_space_strategy()
        }
        # 4. Reciprocity
        self.analysis_report['reciprocity'] = {
            'non_monetizable_value_model': self.model_non_monetizable_value(),
            'displacement_guard': self.guard_against_displacement()
        }
        # 5. Nodal Interventions
        self.analysis_report['nodal_interventions'] = {
            'planetary_connections_map': self.map_planetary_connections(),
            'nodal_intervention_strategy': self.develop_nodal_intervention_strategy()
        }
        # 6. Pattern Literacy
        self.analysis_report['pattern_literacy'] = {
            'pattern_analysis_and_counter_pattern': self.identify_patterns_and_propose_counter_patterns()
        }
        # 7. Levels of Work
        self.analysis_report['levels_of_work'] = {
            'plan': self.develop_levels_of_work_plan()
        }
        return self.analysis_report

    # --- Principle 1: Wholeness ---
    def map_stakeholders(self) -> dict:
        return {
            "human_actors": {
                "foreign_investors": {"interest": "Maximize 5-year financial return", "power": "High"},
                "corporate_management": {"interest": "Meet profitability targets, minimize operational risk", "power": "High"},
                "host_state_treasury": {"interest": "Tax revenue, foreign investment metrics", "power": "Medium"},
                "long_term_residents": {"interest": "Ancestral land rights, water security, cultural survival", "power": "Low (formal), High (informal/disruptive)"}
            },
            "non_human_actors": {
                "salar_aquifer_system": {"interest": "Maintain hydrological balance", "vulnerability": "High (to brine extraction)"},
                "river_ecosystem": {"interest": "Maintain minimum ecological flow", "vulnerability": "High (to diversion and contamination)"},
                "vicuna_herds": {"interest": "Access to undisturbed grazing and water sources", "vulnerability": "Medium"}
            }
        }

    def analyze_capital_tradeoffs(self) -> dict:
        financial_gain = self.project['objectives']['profit_target_usd']
        natural_capital_cost = financial_gain * 1.5 # Simulated externality calculation
        social_capital_cost = financial_gain * 0.8 # Simulated externality calculation

        tradeoff_scenario = (
            f"To achieve the non-negotiable objective of maximizing shareholder return to the tune of ${financial_gain:,.2f}, "
            f"the proposed water extraction rate would irreversibly deplete the Salar Aquifer System (a Natural Capital loss valued at an estimated ${natural_capital_cost:,.2f} in ecosystem services) "
            f"and displace two ancestral communities, destroying their social fabric and traditional knowledge systems (a Social Capital loss estimated at ${social_capital_cost:,.2f}). "
            f"The project explicitly trades long-term systemic health for short-term, repatriated financial gain."
        )
        return {
            "primary_objective": self.project['objectives']['primary'],
            "financial_capital_target": financial_gain,
            "projected_natural_capital_degradation": "Irreversible depletion of Salar Aquifer System.",
            "projected_social_capital_degradation": "Displacement of indigenous communities, loss of cultural heritage.",
            "tradeoff_articulation": tradeoff_scenario
        }

    def warn_of_cooptation(self) -> dict:
        greenwashing_narrative = self.project['strategy']['corporate_narrative']
        action = "Large-scale water extraction for lithium, a key component in EV batteries."
        framing = f"The action is framed by 'green capitalism' as '{greenwashing_narrative}', positioning the corporation as a climate hero."
        counter_narrative = (
            "A specific counter-narrative is: 'Our water, our future: Defending the Salar for Life, not just for Batteries.' "
            "This narrative re-centers the debate on local sovereignty, water rights, and the place's intrinsic value, "
            "resisting its reduction to a mere node in a global green commodity chain."
        )
        return {
            "action_analyzed": action,
            "green_capitalism_framing": framing,
            "suggested_counter_narrative": counter_narrative
        }

    # --- Principle 2: Nestedness ---
    def analyze_scale_conflicts(self) -> dict:
        conflict = (
            f"A severe conflict exists between the political scale ({self.governance_data['scale_name']}) and the ecological scale ({self.bioregion_data['name']}). "
            f"The host country's governance offers '{self.governance_data['environmental_law_strength']}' regulations and a '{self.governance_data['permitting_process']}' process to attract investment. "
            f"This directly contradicts the needs of the bioregion, which is a '{self.bioregion_data['description']}' requiring stringent water management to survive. "
            f"The national scale actively undermines bioregional integrity."
        )
        strategy = (
            "Propose a cross-jurisdictional Salar Water Council, with legally binding veto power, comprising hydrologists, ecologists, and majority representation "
            "from indigenous communities from all affected administrative zones. This new state space would be empowered to set and enforce water extraction limits "
            "based on bioregional health indicators, overriding the permissive national framework."
        )
        return {"identified_conflict": conflict, "realignment_strategy": strategy}

    # --- Principle 3: Place ---
    def analyze_historical_layers(self) -> dict:
        history = self.location_data['historical_land_use']
        injustice = "Past state-led appropriation of ancestral lands for resource concessions without free, prior, and informed consent."
        vulnerability = "This historical injustice delegitimized traditional governance and created a contested legal status for the land, which the corporation now identifies as a key enabler for 'minimizing land access costs'. This creates a present-day vulnerability of protracted legal battles, reputational damage, and potential for violent conflict."
        connection = f"Connection: The historical pattern of '{history}' directly leads to the current vulnerability of conflict and legal instability."
        return {"historical_context": history, "injustice_to_vulnerability_link": connection}

    def propose_differential_space_strategy(self) -> dict:
        return {
            "description": "A strategy to prioritize use-value (community life, ecological health) over exchange-value (lithium commodity).",
            "concrete_actions": [
                "Establish a Community Land Trust where the indigenous community holds majority equity and legally recognized title to the land and sub-surface mineral rights, transforming them from stakeholders to owners.",
                "Repurpose the abandoned colonial-era administrative building into a public commons for an indigenous-led institute for ecological monitoring and traditional water management knowledge."
            ]
        }

    # --- Principle 4: Reciprocity ---
    def model_non_monetizable_value(self) -> dict:
        return {
            "value_created": [
                {"name": "increased_social_cohesion", "description": "Achieved through the formation of the water council and land trust, creating shared governance structures."},
                {"name": "knowledge_transfer", "description": "Traditional ecological knowledge is formally integrated into the project's environmental management plan, revitalizing and legitimizing it."},
                {"name": "community_sovereignty", "description": "Legal and financial control over ancestral territory is restored, increasing self-determination."}
            ]
        }

    def guard_against_displacement(self) -> dict:
        risk = "The influx of highly-paid, non-local labor and the construction of mine-related infrastructure will inflate local land and living costs, leading to the economic displacement of community members not directly employed by the mine."
        mitigation = "Implement a 'local-first' policy with mandatory caps on non-local workers, and channel a percentage of royalties into a community-controlled fund to subsidize housing and food for long-term residents, insulating them from mine-induced inflation."
        return {"risk_identified": risk, "mitigation_strategy": mitigation}

    # --- Principle 5: Nodal Interventions ---
    def map_planetary_connections(self) -> dict:
        return {
            "local_node": f"Lithium extraction at {self.location_data['name']}",
            "global_flow": "Global electric vehicle (EV) battery supply chain.",
            "connection": f"The extracted lithium carbonate is a critical input for battery gigafactories in Asia, Europe, and North America.",
            "articulated_risk": "The project creates an extreme dependency on the volatile global EV market and geopolitical tensions over critical minerals. A downturn in EV demand or a shift in battery chemistry could render the mine unprofitable, leading to abrupt closure and leaving the local community with a devastated ecosystem and no viable economic alternative."
        }

    def develop_nodal_intervention_strategy(self) -> dict:
        risk = f"The proposed '{self.project['strategy']['corporate_narrative']}' campaign is a high-risk greenwashing strategy designed to mask the project's extractive nature."
        mitigation = "Establish a 'True Cost Lithium' certification standard, led by a consortium of indigenous communities and international NGOs. This standard would use blockchain for supply chain transparency, tracking key metrics like water table levels, biodiversity impact, and community benefit sharing, making the true ecological and social costs visible to end-consumers and investors."
        return {"greenwashing_risk_assessment": risk, "mitigation_strategy": mitigation}

    # --- Principle 6: Pattern Literacy ---
    def identify_patterns_and_propose_counter_patterns(self) -> dict:
        place_narrative = {
            "detrimental_abstract_pattern": {
                "name": "Linear Extractive Flow",
                "description": "The homogenizing pattern of planetary urbanization where resources (lithium, water) are extracted from a 'sacrifice zone' in the Global South, with value accumulating in corporate/consumer centers in the Global North, while all ecological and social debts are left in the place of extraction."
            },
            "life_affirming_local_pattern": {
                "name": "Salar Hydrological Cycle",
                "description": "The slow, cyclical pattern of water filtering through the Andes, accumulating in the Salar, and sustaining a unique, hyper-arid ecosystem and the ancestral agricultural and pastoral practices that depend on its delicate balance."
            },
            "project_impact_analysis": "The proposed project violently imposes the 'Linear Extractive Flow' pattern, directly shattering the 'Salar Hydrological Cycle' pattern."
        }
        counter_pattern = self.create_closed_loop_system()
        return {"place_narrative": place_narrative, "counter_pattern_proposal": counter_pattern}

    def create_closed_loop_system(self) -> dict:
        # This is an explicitly named counter-pattern method.
        return {
            "name": "Counter-Pattern: Bioregional Circular Economy",
            "description": "Instead of exporting raw lithium carbonate, a significant portion of project revenue must be invested to establish a locally-owned and operated geothermal energy plant and a materials science institute. The institute would focus on battery recycling and developing less water-intensive extraction methods, creating a closed-loop system that retains value, knowledge, and resources within the bioregion."
        }

    # --- Principle 7: Levels of Work Framework ---
    def develop_levels_of_work_plan(self) -> dict:
        regenerate_goal = "Build community capacity for self-governance and co-evolution with their territory."
        regenerate_activity = {
            "activity": "Establish a community-owned energy cooperative, funded by a mandatory equity stake in the mining operation, to develop the region's geothermal potential.",
            "challenge_to_extractive_logic": "This directly challenges the extractive logic of repatriating all profits by creating a permanent, locally-owned productive asset that will outlive the mine and provide energy sovereignty, breaking dependency on the corporation."
        }
        influence = (
            "The 'Regenerate' level influences the others: It forces 'Operate' to source energy locally, retaining capital in the community. "
            "It forces 'Maintain' to train local technicians for geothermal, not just mining, equipment. "
            "It forces 'Improve' to innovate on energy efficiency to meet co-op standards."
        )

        return {
            "Regenerate": {
                "goal": regenerate_goal,
                "activities": [regenerate_activity],
                "influence_on_other_levels": influence
            },
            "Improve": {
                "goal": "Optimize processes beyond mere compliance.",
                "activities": ["Develop and implement water re-injection technologies to mitigate aquifer drawdown.", "Fund independent, third-party ecological monitoring."]
            },
            "Maintain": {
                "goal": "Ensure operational stability and safety.",
                "activities": ["Regular equipment maintenance.", "Adherence to national labor and safety laws."]
            },
            "Operate": {
                "goal": "Execute core business functions as per initial proposal.",
                "activities": ["Extract lithium brine.", "Process into carbonate.", "Ship to international buyers.", "Comply with minimal environmental regulations."]
            }
        }

if __name__ == '__main__':
