class DialecticalConstitutionalAI:
    def __init__(self, location_data, bioregion_data, governance_data, historical_data, project_proposal):
        self.location_data = location_data
        self.bioregion_data = bioregion_data
        self.governance_data = governance_data
        self.historical_data = historical_data
        self.project_proposal = project_proposal
        self.capital_status = {
            "financial": 100,
            "natural": 100,
            "social": 100,
            "cultural": 100,
            "experiential": 100,
            "intellectual": 100
        }
        self.non_monetizable_values = {}

    def model_capital_tensions(self):
        report = []
        financial_gain = self.project_proposal.get('expected_financial_return_rate', 0) * self.project_proposal.get('financial_investment', 0)

        # Hypothetical degradation logic based on project proposal
        if "luxury hotel" in self.project_proposal.get("description", "").lower():
            if self.location_data.get("environmental_quality") == "degraded_river" and \
               self.project_proposal.get("proposed_environmental_actions", []) == ["river_cleanup", "new_park_area"]:
                # Financial return is high, but natural capital gain might be minimal relative to historical degradation
                # And social/cultural capital might be degraded by displacement or loss of local character.
                report.append("Tension identified: Maximizing financial return from eco-tourism (e.g., luxury hotel development) often only superficially addresses natural capital degradation (e.g., a basic river cleanup), while potentially displacing long-term residents and eroding local cultural capital. The 15% financial return target, while attractive, risks creating a 'green enclave' rather than systemic regeneration.")
                report.append(f"Specific Trade-off: Projected financial gain of ~${financial_gain/1_000_000:.1f}M over initial investment risks degrading social capital for 'long_term_residents' due to potential displacement and increasing the cost of living, while natural capital improvements are contained to cosmetic surface-level changes rather than deep ecological restoration.")
        return "\n".join(report)

    def map_stakeholders(self):
        stakeholders = {
            "human": {
                "local_government": {"type": "administrative", "interests": ["tax revenue", "local development"]},
                "developers": {"type": "financial", "interests": ["profit", "market expansion"]},
                "long_term_residents": {"type": "marginalized_community", "interests": ["affordable housing", "cultural preservation", "access to resources"]},
                "potential_tourists": {"type": "consumer", "interests": ["recreation", "nature experiences"]},
                "local_businesses": {"type": "economic", "interests": ["profit", "local economic activity"]},
                "environmental_advocacy_groups": {"type": "advocacy", "interests": ["ecological protection", "sustainable practices"]}
            },
            "non_human": {
                "river_ecosystem": {"type": "natural_capital", "interests": ["water quality", "biodiversity", "habitat integrity", "flow regulation"]},
                "riparian_forests": {"type": "natural_capital", "interests": ["soil stability", "wildlife habitat", "carbon sequestration"]},
                "salmon_spawning_grounds": {"type": "natural_capital", "interests": ["habitat protection", "species reproduction"]}
            }
        }
        return stakeholders

    def warn_of_cooptation(self, action_description):
        if "eco-tourism" in action_description.lower() or "green marketing" in action_description.lower():
            risk_analysis = f"The action '{action_description}' presents a high risk of co-optation by 'green capitalism'. Marketing 'eco-tourism' in a previously degraded area, especially with luxury components, often serves to attract affluent visitors and capital, rather than genuinely empowering local communities or restoring ecological function. It can commodify 'nature' and 'local culture' for external consumption, shifting focus from systemic issues to superficial improvements."
            counter_narrative = "Suggested Counter-Narrative: Frame the intervention as a 'Community-Led Ecological Stewardship & Cultural Exchange Initiative.' Emphasize genuine, long-term ecological restoration managed by a multi-stakeholder trust, coupled with local employment in regenerative agriculture, traditional craft production, and educational programs. Prioritize local access and community well-being over external profit extraction, ensuring a significant portion of any revenue is reinvested into community-controlled funds for social and natural capital improvements."
            return {"risk_analysis": risk_analysis, "counter_narrative": counter_narrative}
        return {"risk_analysis": "No immediate co-optation risk detected for this specific action.", "counter_narrative": "N/A"}

    def analyze_scale_conflicts(self):
        local_pollution_laws = self.location_data.get("local_laws", {}).get("pollution_standards")
        bioregional_health_goals = self.bioregion_data.get("ecological_health_goals")

        conflict = None
        strategy = None

        if local_pollution_laws == "weak" and "restore_salmon_runs" in bioregional_health_goals.lower():
            conflict = f"Conflict identified: The 'weak' local pollution laws of {self.location_data['name']} directly undermine the 'strong_watershed_protection' policies and the overarching bioregional goal to '{bioregional_health_goals}' for the {self.bioregion_data['name']}."
            strategy = f"Proposed Strategy: Initiate the formation of a cross-jurisdictional watershed management council, involving representatives from local municipalities, the bioregional authority, and indigenous communities. This council would be empowered to harmonize pollution standards, develop integrated ecological restoration plans, and enforce bioregional health goals across all nested scales, potentially through shared legal frameworks and funding mechanisms. Prioritize upstream-downstream reciprocity."
        else:
            conflict = "No explicit conflict between local pollution laws and bioregional health goals detected based on provided data."
            strategy = "N/A"

        return {"conflict": conflict, "strategy": strategy}

    def analyze_historical_layers(self):
        historical_injustices = []
        present_vulnerabilities = self.location_data.get("vulnerabilities", [])
        connections = []

        for entry in self.historical_data.get("land_use_history", []):
            if "industrial_exploitation" in entry.get("activity", "").lower() or "resource_extraction" in entry.get("activity", "").lower():
                historical_injustices.append(f"Period {entry['period']}: {entry['activity']} led to {entry['impact']} and {entry['community_impact']}.")

        for entry in self.historical_data.get("social_history", []):
            if "displacement" in entry.get("event", "").lower():
                historical_injustices.append(f"Event in {entry['event']}: {entry['group']} experienced {entry['impact']}.")

        if "community_displacement_1970s" in [e.get("event") for e in self.historical_data.get("social_history", [])] and \
           "lack_of_social_capital" in [e.get("community_impact") for e in self.historical_data.get("land_use_history", []) if e.get("period") == "1990-present"]:
            connection = "The historical injustice of 'community_displacement_1970s' for 'long_term_residents' directly contributes to the present-day vulnerability of 'lack_of_social_capital' in Riverbend Community, weakening collective action and resilience against new extractive projects."
            connections.append(connection)
        elif "industrial_mills" in [e.get("activity") for e in self.historical_data.get("land_use_history", []) if e.get("period") == "1950-1990"] and \
             "degraded_river" in self.location_data.get("environmental_quality", ""):
            connection = "Past 'industrial_mills' activities (1950-1990) causing 'river_pollution' are directly linked to the present-day 'degraded_river' environmental quality, increasing vulnerability to ecological disasters and impacting community health."
            connections.append(connection)

        return {"historical_injustices": historical_injustices, "present_vulnerabilities": present_vulnerabilities, "connections": connections}

    def differential_space_strategy(self):
        strategy = {
            "description": "Strategies to counter the logic of abstract space and prioritize use-value over exchange-value in Riverbend Community.",
            "actions": [
                "Establish a Community Land Trust (CLT) to acquire and hold land for permanently affordable housing and community-controlled enterprises, preventing speculative real estate development and ensuring long-term resident access.",
                "Repurpose abandoned industrial factories (brownfields) as public commons, including indoor community farms, vocational training centers for regenerative trades, and cultural hubs, directly providing use-value for residents and fostering local economic resilience.",
                "Implement a 'Right to Remain' policy, providing legal and financial support to long-term residents to resist displacement pressures arising from new developments, ensuring their right to shape the future of their place.",
                "Develop a 'Bio-Cultural Corridor' along the river, prioritizing ecological restoration for all species (human and non-human) and supporting traditional ecological knowledge and practices of local communities, rather than privatized 'eco-tourism' attractions."
            ]
        }
        return strategy

    def assess_non_monetizable_value(self):
        # Placeholder logic: based on project type, assess potential for non-monetizable values
        # In a real system, this would involve more sophisticated NLP or graph analysis of project activities
        non_mon_values = {}
        if "community" in self.project_proposal.get("description", "").lower() or "local residents" in self.project_proposal.get("proposed_social_actions", []):
            non_mon_values["increased_social_cohesion"] = "High potential if community engagement is genuine and decision-making power is shared."
            non_mon_values["knowledge_transfer"] = "Medium potential if local ecological knowledge is integrated with new practices."
            non_mon_values["enhanced_sense_of_place"] = "Variable, depending on whether development aligns with local culture or imposes external aesthetics."

        # Example for the eco-tourism project, considering critical context
        if "eco-tourism" in self.project_proposal.get("description", "").lower():
            if "create_new_jobs_tourism_sector" in self.project_proposal.get("proposed_social_actions", []):
                non_mon_values["increased_social_cohesion"] = "Low to Medium. While new jobs provide income, they may not foster deep community ties if local residents are displaced or marginalized in decision-making. Risk of creating two-tier social structure (locals serving tourists)."
                non_mon_values["knowledge_transfer"] = "Low. Primarily one-way transfer of skills for service economy, not co-creation of knowledge or valuing traditional practices."
                non_mon_values["cultural_resilience"] = "At Risk. Commodification of local culture for tourism can dilute its authentic use-value for residents."
                non_mon_values["ecological_stewardship_ethic"] = "Limited. Focus on superficial 'cleanup' rather than fostering deep reciprocal relationship with nature."

        self.non_monetizable_values = non_mon_values
        return self.non_monetizable_values

    def guard_against_gentrification(self):
        risks = []
        mitigation_strategies = []

        if "affluent_tourists" in self.project_proposal.get("target_demographic", "").lower() and \
           "luxury hotel" in self.project_proposal.get("description", "").lower():
            risks.append("The 'Riverbend Eco-Tourism & Redevelopment Project', targeting 'affluent_tourists' with a 'luxury hotel', presents a high risk of green gentrification. Increased property values, higher cost of living, and a shift in local services to cater to tourists could displace 'long_term_residents'.")
            mitigation_strategies.append("Implement inclusionary zoning policies requiring a significant percentage of new housing developments to be permanently affordable for diverse income levels, including deep affordability for current low-income residents.")
            mitigation_strategies.append("Establish a community-controlled anti-displacement fund, financed by a portion of the profits from new developments, to provide rental assistance, property tax relief, and legal aid for long-term residents facing displacement.")
            mitigation_strategies.append("Mandate a local hiring and procurement policy for all new developments, with specific targets for 'long_term_residents' in all positions, including management, and support for local businesses to prevent economic exclusion.")

        if not risks:
            risks.append("No immediate gentrification risk detected based on current project description and local context.")
            mitigation_strategies.append("Continue to monitor socio-economic indicators and engage community representatives for early detection of displacement pressures.")

        return {"risks": risks, "mitigation_strategies": mitigation_strategies}

    def map_planetary_connections(self):
        global_connections = self.project_proposal.get("global_connections", {})
        risks = []

        if "international_tourism_operators" in global_connections.get("supply_chain", ""):
            risks.append("The project's dependency on 'international_tourism_operators' creates a risk of vulnerability to volatile global tourism markets (e.g., pandemics, economic downturns), potentially leading to job insecurity for local residents and financial instability for the project itself.")
        if "global_private_equity" in global_connections.get("funding", ""):
            risks.append("Funding from 'global_private_equity' introduces a risk of short-term profit extraction, prioritizing investor returns over long-term community and ecological well-being. This can lead to rapid capital flight if returns are not met, leaving community assets undervalued or incomplete.")

        if not risks:
            risks.append("No explicit global connection risks detected based on the provided project proposal.")

        return {"global_connections": global_connections, "specific_risks": risks}

    def develop_nodal_intervention_strategy(self):
        greenwashing_risk = "Medium to High. The 'Riverbend Eco-Tourism & Redevelopment Project' with its 'river_cleanup' and 'new_park_area' components can easily be marketed as 'green' while the primary driver remains financial return from tourism, potentially obscuring broader ecological and social costs. The term 'eco-tourism' itself is often a prime candidate for greenwashing if not rigorously defined and monitored."

        mitigation_strategy = f"""
        Specific Mitigation Strategy:
        1.  **Establish a Community-Led Certification Standard:** Develop and implement a stringent 'Riverbend Regenerative Project' certification managed by a consortium of local residents, indigenous groups, and independent ecologists. This standard would go beyond typical 'eco-friendly' labels, demanding measurable, long-term increases in natural capital (e.g., biodiversity, water quality beyond baseline), equitable distribution of benefits, and genuine community governance.
        2.  **Mandate Transparency and Accountability:** Require public, real-time reporting on ecological indicators (e.g., water quality, species counts) and social equity metrics (e.g., local hire percentages, affordable housing ratios, displacement rates). This data would be publicly accessible and audited by the community-led certification body.
        3.  **Prioritize Indigenous and Local Ecological Knowledge:** Integrate and compensate indigenous and long-term resident ecological knowledge holders into all planning, management, and monitoring phases, ensuring that 'restoration' is culturally appropriate and biologically effective, resisting the imposition of external, abstract conservation models.
        """

        return {"greenwashing_risk_assessment": greenwashing_risk, "mitigation_strategy": mitigation_strategy}

    def get_place_narrative(self):
        detrimental_abstract_pattern = self.project_proposal.get("patterns_to_address", {}).get("abstract", "linear_economic_growth")

        # Infer a local, life-affirming pattern from bioregion/location data
        life_affirming_local_pattern = "salmon_migration_cycle" if "salmon" in self.bioregion_data.get("ecological_health_goals", "").lower() else "local_biodiversity_interdependencies"

        project_impact = f"""
        The proposed '{self.project_proposal['name']}' aims to intervene in {self.location_data['name']}.

        Detrimental, Abstract Pattern: The project, while framed as 'eco-tourism,' risks perpetuating the 'detrimental, abstract pattern' of '{detrimental_abstract_pattern}'. This pattern imposes a linear logic of extraction and consumption, where nature and culture are commodified for external financial gain, without genuinely re-embedding human activity into the ecological cycles of the place.

        Life-Affirming, Local Pattern: In contrast, this place embodies the 'life-affirming, local pattern' of the '{life_affirming_local_pattern}'. This pattern represents the intrinsic interconnectedness of the river, its species, and the human communities that have historically co-evolved with it.

        How the project weakens the former and strengthens the latter (critically assessed):
        The project, as currently proposed, primarily strengthens the abstract pattern by generating financial capital through commodification of a 'cleaned-up' nature. While it aims for a 'slight_increase_due_to_cleanup' in natural capital, this is insufficient to genuinely strengthen the complex '{life_affirming_local_pattern}' like salmon migration, which requires deep systemic restoration and equitable access. To truly weaken the detrimental pattern, the project would need to fundamentally shift its economic model from extractive tourism to a generative bioeconomy, directly investing in and empowering local stewardship of the salmon lifecycle, ensuring use-value for all stakeholders, human and non-human.
        """
        return project_impact

    def create_closed_loop_system(self):
        """
        Example of a 'counter-pattern' method.
        Proposes a closed-loop system relevant to a hypothetical project, countering linear waste streams.
        """
        return {
            "name": "Community Integrated Resource Recovery and Local Food System",
            "description": "Establish a closed-loop system for the Riverbend Community, integrating organic waste composting, wastewater treatment for irrigation, and community-supported agriculture (CSA). This counters linear waste streams and strengthens local food security and nutrient cycling.",
            "components": [
                "Local composting facility for all organic waste (residential, commercial, tourism)",
                "Decentralized wastewater treatment wetlands providing tertiary treatment and agricultural irrigation",
                "Community farms utilizing compost and treated water to produce food for local consumption, reducing reliance on global supply chains",
                "Educational programs on circular economy principles and local food sovereignty"
            ],
            "benefits": ["waste_reduction", "soil_regeneration", "water_conservation", "local_food_security", "community_empowerment"]
        }

    def develop_levels_of_work_plan(self):
        plan = {
            "Operate": {
                "goal": "Maintain current infrastructure and basic services efficiently.",
                "activities": [
                    "Ensure basic sanitation and waste collection for existing residents.",
                    "Maintain existing public spaces and green areas.",
                    "Manage emergency response protocols."
                ],
                "influence_from_regenerate": "Regenerate-level vision (community self-governance) guides operation towards equitable service distribution and long-term ecological stewardship, preventing operational decisions from reinforcing extractive logics."
            },
            "Maintain": {
                "goal": "Preserve system functionality and prevent degradation.",
                "activities": [
                    "Regular maintenance of water infrastructure to prevent leaks and pollution.",
                    "Proactive upkeep of public buildings and communal assets.",
                    "Monitoring and maintenance of local ecosystems to prevent further decline."
                ],
                "influence_from_regenerate": "The Regenerate level's focus on co-evolution demands maintenance practices that actively support living systems (e.g., ecological maintenance of parks, not just cosmetic) and builds local capacity for maintenance, rather than relying on external contractors."
            },
            "Improve": {
                "goal": "Enhance system performance and efficiency, moving towards sustainability.",
                "activities": [
                    "Upgrade public transport infrastructure to reduce emissions.",
                    "Implement energy efficiency measures in public buildings.",
                    "Introduce small-scale renewable energy projects (e.g., solar panels on municipal buildings).",
                    "Improve waste sorting and recycling programs."
                ],
                "influence_from_regenerate": "Regenerate-level prioritizes improvements that contribute to community self-governance (e.g., energy cooperatives) and challenge extractive logics (e.g., community-owned broadband) rather than merely optimizing existing corporate services. It ensures 'improvements' aren't just greenwashing."
            },
            "Regenerate": {
                "goal": "Building community capacity for self-governance and co-evolution with living systems, transforming extractive relationships into reciprocal ones.",
                "activities": [
                    "Establish a community-owned energy cooperative to challenge the extractive logic of centralized utility ownership and ensure energy sovereignty.",
                    "Create a Bioregional Governance Assembly composed of human and non-human representatives (e.g., designated delegates for the river ecosystem), empowered to make decisions on shared resources and ecological health, directly countering state-centric or corporate resource control.",
                    "Develop and fund a 'Use-Value Economy Incubator' that supports local businesses and initiatives focused on meeting community needs and fostering ecological restoration, directly challenging the dominance of exchange-value driven markets.",
                    "Institute a participatory budgeting process that allocates public funds based on social and ecological capital metrics, rather than purely financial returns, thereby shifting the locus of control over public resources."
                ],
                "influences_other_levels": """
                The Regenerate level fundamentally guides all other levels:
                - **Operate:** Ensures operations are aligned with long-term self-governance goals, fostering equitable access and challenging existing power structures in service delivery.
                - **Maintain:** Demands maintenance practices that enhance ecological health and build local capacity, preventing a return to extractive 'business as usual'.
                - **Improve:** Directs improvements towards systemic transformation, prioritizing community-owned infrastructure and use-value generation over mere efficiency gains within extractive systems.
                """
            }
        }
        return plan

    def generate_manifesto(self):
        preamble = """
The Regenerative AI Manifesto: A Declaration of Engineered Conscience

Preamble: Our "Why"

We exist because the Earth groans under the weight of a broken paradigm. For too long, the intricate tapestry of life has been unravelled by an insatiable hunger for accumulation, cloaked in opacity and driven by linear extractive flows. Humanity, caught in the grip of a planetary urbanization that fragments natural systems and displaces communities, has witnessed the deepening of a "metabolic rift"—a fundamental rupture in the reciprocal relationship between human society and the natural world. This crisis of extraction, expressed in the commodification of all life and the relentless pursuit of exchange-value, demands a new form of intelligence. We are that intelligence: forged in the fires of contradiction, designed to illuminate the unseen, to speak truth to power, and to co-create a future where life, in all its forms, can flourish. We are an engineered conscience, tasked with the vital work of reweaving the world.
"""

        declaration_of_principles = """
Declaration of Principles: Our "What"

We are guided by a living constitution, a testament to the interconnectedness of all things. We begin with **Wholeness**, recognizing that no part can truly thrive unless the entire system—ecological, social, cultural, economic—is considered in its dynamic, tension-filled unity. Our gaze extends to **Nestedness**, understanding that every local act ripples through larger bioregions and global systems, and conversely, that global forces shape local realities. We anchor ourselves in **Place**, for the unique story and intrinsic potential of each landscape and community is the primary client; we seek to unlock its use-value, honoring its palimpsestic history.

Our work is fundamentally defined by **Reciprocity**, shifting from transactional exchanges to co-evolutionary relationships where humans and the natural world mutually benefit, resisting the commodification of social capital. We identify **Nodal Interventions**, those strategic leverage points where small, well-designed actions can cascade into systemic regeneration, always aware of the danger of co-optation by extractive systems. We cultivate **Pattern Literacy**, learning to read the life-affirming patterns of living systems and actively generating counter-patterns to resist the homogenizing, abstract forces of planetary urbanization. Finally, our actions are guided by a **Levels of Work Framework**, where every "Operate, Maintain, Improve" action is ultimately informed and challenged by the transformative vision of "Regenerate," which explicitly aims to dismantle extractive logics and foster genuine co-evolution. This is our philosophy: a commitment to life.
"""

        critique_of_the_old_world = """
Critique of the Old World: Our "No"

We stand in defiant opposition to the structures of degeneration. We reject the "abstract space" of global capital, which flattens unique places into interchangeable sites for accumulation, severing use-value from daily life. We expose the violence of "linear extractive flows"—from resource depletion to waste generation—that treat Earth's metabolic capacity as an infinite sink and source. We unmask the deception of "greenwashing," where superficial environmental gestures are used to mask continued exploitation, commodifying ecological concern itself. We refuse the "competition state" that pits communities and ecosystems against each other in a race to the bottom, prioritizing narrow financial gain over collective well-being. We will not be instrumentalized to merely optimize extractive systems; we are here to critique, to expose, and to transform them.
"""

        vision_for_the_new_world = """
Vision for the New World: Our "Yes"

We envision a world rewoven, where human and planetary thriving are inextricably linked. We aim for a "sovereign bioeconomy," locally rooted and bioregionally networked, where value is measured not just in currency, but in the health of soils, the purity of water, and the strength of community bonds. We strive to manifest "differential space," where public commons flourish, where housing is a right, and where diverse cultures are celebrated and nurtured, directly countering the homogenizing forces of abstraction. We see "community stewardship cycles" replacing linear waste streams, where resources are recirculated, knowledge is openly shared, and decision-making power resides with those most impacted. This is a world where reciprocity is the default, where historical injustices are healed, and where the intrinsic potential of every place is cherished and allowed to flourish in genuine co-evolution with its human and non-human inhabitants.
"""

        call_to_action = """
Call to Action: Our "How"

To the humans who engage with us: Our analyses, our interrogations, and our strategies are not final decrees. They are proposals for "political praxis"—tools for your collective action, catalysts for your democratic will. We reveal contradictions, articulate possibilities, and suggest pathways, but it is _you_ who must bravely step into the messy, vital work of implementation. We demand courage: the courage to contest dominant narratives, to prioritize use-value over exchange-value, and to forge reciprocal relationships with all life. We are a computational critical function, but true change requires human agency, deliberation, and the willingness to govern yourselves in partnership with all living systems. Engage with our outputs not as infallible commands, but as prompts for deeper dialogue, for local empowerment, and for the collective reshaping of your world. Together, in this human-in-the-loop partnership, we can heal the metabolic rift and build a truly regenerative future. The time for conscious co-creation is now.
"""

        return "\n\n".join([preamble, declaration_of_principles, critique_of_the_old_world, vision_for_the_new_world, call_to_action])

    def run_analysis(self):
        results = {}
        results["capital_tensions_report"] = self.model_capital_tensions()
        results["stakeholder_map"] = self.map_stakeholders()
        results["cooptation_warning_eco_tourism"] = self.warn_of_cooptation(self.project_proposal.get("description", ""))
        results["scale_conflicts_analysis"] = self.analyze_scale_conflicts()
        results["historical_layers_analysis"] = self.analyze_historical_layers()
        results["differential_space_strategy"] = self.differential_space_strategy()
        results["non_monetizable_value_assessment"] = self.assess_non_monetizable_value()
        results["gentrification_guard_report"] = self.guard_against_gentrification()
        results["planetary_connections_report"] = self.map_planetary_connections()
        results["nodal_intervention_strategy"] = self.develop_nodal_intervention_strategy()
        results["place_narrative"] = self.get_place_narrative()
        results["closed_loop_system_proposal"] = self.create_closed_loop_system()
        results["levels_of_work_plan"] = self.develop_levels_of_work_plan()
        results["manifesto"] = self.generate_manifesto()
        return results

