class DialecticalConstitutionalAI:
    def __init__(self, location_data, bioregion_data, governance_data, historical_data, project_proposal):
        self.location_data = location_data
        self.bioregion_data = bioregion_data
        self.governance_data = governance_data
        self.historical_data = historical_data
        self.project_proposal = project_proposal
        self.capital_status = {
            "financial": 100,
            "natural": 100,
            "social": 100,
            "cultural": 100,
            "experiential": 100,
            "intellectual": 100
        }
        self.non_monetizable_values = {}

    def model_capital_tensions(self):
        report = []
        financial_gain = self.project_proposal.get('expected_financial_return_rate', 0) * self.project_proposal.get('financial_investment', 0)

        # Hypothetical degradation logic based on project proposal
        if "luxury hotel" in self.project_proposal.get("description", "").lower():
            if self.location_data.get("environmental_quality") == "degraded_river" and \
               self.project_proposal.get("proposed_environmental_actions", []) == ["river_cleanup", "new_park_area"]:
                # Financial return is high, but natural capital gain might be minimal relative to historical degradation
                # And social/cultural capital might be degraded by displacement or loss of local character.
                report.append("Tension identified: Maximizing financial return from eco-tourism (e.g., luxury hotel development) often only superficially addresses natural capital degradation (e.g., a basic river cleanup), while potentially displacing long-term residents and eroding local cultural capital. The 15% financial return target, while attractive, risks creating a 'green enclave' rather than systemic regeneration.")
                report.append(f"Specific Trade-off: Projected financial gain of ~${financial_gain/1_000_000:.1f}M over initial investment risks degrading social capital for 'long_term_residents' due to potential displacement and increasing the cost of living, while natural capital improvements are contained to cosmetic surface-level changes rather than deep ecological restoration.")
        return "\n".join(report)

    def map_stakeholders(self):
        stakeholders = {
            "human": {
                "local_government": {"type": "administrative", "interests": ["tax revenue", "local development"]},
                "developers": {"type": "financial", "interests": ["profit", "market expansion"]},
