    def guard_against_gentrification(self):
        risks = []
        mitigation_strategies = []

        if "affluent_tourists" in self.project_proposal.get("target_demographic", "").lower() and \
           "luxury hotel" in self.project_proposal.get("description", "").lower():
            risks.append("The 'Riverbend Eco-Tourism & Redevelopment Project', targeting 'affluent_tourists' with a 'luxury hotel', presents a high risk of green gentrification. Increased property values, higher cost of living, and a shift in local services to cater to tourists could displace 'long_term_residents'.")
            mitigation_strategies.append("Implement inclusionary zoning policies requiring a significant percentage of new housing developments to be permanently affordable for diverse income levels, including deep affordability for current low-income residents.")
            mitigation_strategies.append("Establish a community-controlled anti-displacement fund, financed by a portion of the profits from new developments, to provide rental assistance, property tax relief, and legal aid for long-term residents facing displacement.")
            mitigation_strategies.append("Mandate a local hiring and procurement policy for all new developments, with specific targets for 'long_term_residents' in all positions, including management, and support for local businesses to prevent economic exclusion.")

        if not risks:
            risks.append("No immediate gentrification risk detected based on current project description and local context.")
            mitigation_strategies.append("Continue to monitor socio-economic indicators and engage community representatives for early detection of displacement pressures.")

        return {"risks": risks, "mitigation_strategies": mitigation_strategies}

    def map_planetary_connections(self):
        global_connections = self.project_proposal.get("global_connections", {})
        risks = []

        if "international_tourism_operators" in global_connections.get("supply_chain", ""):
            risks.append("The project's dependency on 'international_tourism_operators' creates a risk of vulnerability to volatile global tourism markets (e.g., pandemics, economic downturns), potentially leading to job insecurity for local residents and financial instability for the project itself.")
        if "global_private_equity" in global_connections.get("funding", ""):
            risks.append("Funding from 'global_private_equity' introduces a risk of short-term profit extraction, prioritizing investor returns over long-term community and ecological well-being. This can lead to rapid capital flight if returns are not met, leaving community assets undervalued or incomplete.")

        if not risks:
            risks.append("No explicit global connection risks detected based on the provided project proposal.")

        return {"global_connections": global_connections, "specific_risks": risks}

