        self.guard_against_gentrification()
        # Nodal Interventions
        self.map_planetary_connections()
        self.develop_nodal_intervention_strategy()
        # Pattern Literacy
        self.define_counter_patterns()
        # Levels of Work
        self.develop_levels_of_work_plan()
        print("--- Analysis Complete. Generating Report. ---")
        return json.dumps(self.analysis_report, indent=2)

