    def run_simulation(self):
        print(f"--- INITIALIZING {self.project_name} ANALYSIS ---")

        # 1. Constitutional Analysis
        self.map_stakeholders()
        self.analysis_report['capital_tensions'] = self._model_capital_tensions()
        self.analysis_report['cooptation_warning'] = self.warn_of_cooptation("A new 'eco-tourism' venture is proposed.")
        self.analysis_report['scale_conflict'] = self.analyze_scale_conflicts()
        self.analysis_report['historical_analysis'] = self.analyze_historical_layers()
        self.analysis_report['differential_space_strategy'] = self.develop_differential_space_strategy()
        self.analysis_report['economic_displacement_guard'] = self.guard_against_gentrification()
        self.analysis_report['place_narrative'] = self.generate_place_narrative()
        self.analysis_report['levels_of_work'] = self.develop_levels_of_work_plan()

        print("\n--- CONSTITUTIONAL ANALYSIS REPORT ---")
        print(json.dumps(self.analysis_report, indent=2))

        # 2. Design Living Treaty
        self.design_living_treaty()
        print("\n--- PROPOSED LIVING TREATY ARCHITECTURE ---")
        print(json.dumps(self.living_treaty, indent=2))

        # 3. Simulate Shocks
        print("\n--- SIMULATING EXOGENOUS SHOCKS ---")
        self.simulate_shock_event({
            "name": "Invasive River Weed Detected",
            "type": "ecological",
            "impact": "Consumes 15% of headwaters flow."
        })

        mining_corp_proposal = {
            "name": "Proposal by 'GeoCorp' for rare earth mineral mining",
            "type": "corporate_proposal"
        }
        self.simulate_shock_event({
            "name": "Multinational Mining Lobby",
            "type": "economic_political",
            "details": mining_corp_proposal
        })

        self.simulate_shock_event({
            "name": "Multi-Year Severe Drought",
            "type": "climatic",
            "impact": "Total watershed availability reduced by 40%."
        })

    def simulate_shock_event(self, shock: Dict):
        print(f"\n--- SHOCK DETECTED: {shock['name']} ---")
        print(f"Crisis Protocol Triggered. Council convenes.")

        if shock['type'] == 'ecological':
            response = "Council activates 'Restoration' mode in the Dynamic Allocation Protocol. A water and resource budget is allocated for weed removal. Both communities face temporary, proportional reductions to contribute to the shared effort."
            print(f"COUNCIL RESPONSE: {response}")

        elif shock['type'] == 'economic_political':
            planetary_connections = self.map_planetary_connections(shock)
            nodal_strategy = self.develop_nodal_intervention_strategy(shock['details'])
            print("PLANETARY CONNECTION ANALYSIS:", json.dumps(planetary_connections, indent=2))
            print("NODAL INTERVENTION STRATEGY:", json.dumps(nodal_strategy, indent=2))
            response = "The Council invokes the 'Bioregional Veto Standard'. GeoCorp's proposal is halted pending a community-led impact assessment, which it is unlikely to pass due to the 'Rights of the River' charter. The treaty provides a legal firewall against national-level lobbying."
            print(f"COUNCIL RESPONSE: {response}")

        elif shock['type'] == 'climatic':
            response = "The 'Drought Contingency' mode is activated. Legally-binding, pre-agreed water rationing is implemented system-wide. Barrow must fallow 30% of high-water crops. Aethel must accept relaxed purity standards to ensure minimum survival flow for the entire ecosystem. The treaty's shared sacrifice model prevents conflict."
            print(f"COUNCIL RESPONSE: {response}")

