import json

# --- DATA OBJECTS REPRESENTING THE POLITICAL-ECONOMIC & ECOLOGICAL CONTEXT ---

LOCATION_DATA = {
    "name": "Valle Seco",
    "country": "Fictional Latin American Nation",
    "economy": {
        "primary_exports": ["avocados", "berries"],
        "export_value_usd": 450_000_000,
        "dominant_actors": ["Large-Scale Agribusiness Exporters", "International ESG Investors"],
        "local_livelihoods": ["Small-scale subsistence farming", "Farm labor", "Water truck delivery services"]
    },
    "sociology": {
        "demographics": ["Indigenous farming communities", "Mestizo smallholders", "Migrant laborers"],
        "vulnerabilities": {
            "water_access": "Severely restricted; dependency on trucked water for 70% of local population.",
            "social_capital": "Low due to historical displacement and conflict over resources.",
            "land_tenure": "Highly concentrated ownership by agribusiness."
        }
    },
    "history": {
        "historical_land_use": "colonial_land_grant_system",
        "recent_history": "Neoliberal deregulation in 1980s leading to water rights privatization."
    }
}

BIOREGION_DATA = {
    "name": "Aconcagua Endorheic Basin Analogue",
    "hydrology": {
        "primary_river_status": "Functionally dry for 8-10 months/year.",
        "aquifer_status": "Critically overdrawn, declining 1.5m annually.",
        "watershed_health_goals": "Restore perennial river flow; achieve stable aquifer levels; regenerate riparian ecosystems."
    },
    "ecology": {
        "native_biodiversity": "Threatened by monoculture expansion and habitat loss.",
        "keystone_pattern": "Seasonal flood pulse, critical for groundwater recharge and ecosystem renewal (currently suppressed)."
    }
}

GOVERNANCE_DATA = {
    "national_water_code": {
        "framework": "Market-based allocation; water rights are private, tradable property, separate from land.",
        "enforcement": "Weak; favors powerful economic actors over ecological or community needs."
    },
    "municipal_regulations": {
        "land_use_zoning": "Allows for expansion of intensive agriculture.",
        "pollution_laws": "Lax enforcement on agricultural runoff."
    },
    "planetary_connections": {
        "supply_chains": "Direct links to supermarket chains in North America, EU, and Asia.",
        "financial_circuits": "Funded by global investment funds seeking 'green' assets."
    }
}


class AguaCulturaProtocol:
    """
    A framework for a regenerative hinterland, designed to be critically aware
    and compliant with the Critically-Informed Regenerative Constitution v2.1.
    """

    def __init__(self, location_data, bioregion_data, governance_data):
        # Principle 2 (Nestedness): Accepts ecological and political scales
        self.location = location_data
        self.bioregion = bioregion_data
        self.governance = governance_data
        self.proposal = {"title": "The Agua-Cultura Protocol: A Framework for a Regenerative Hinterland"}
        # Principle 3 (Place): Loads config from data objects reflecting history
        self.historical_context = self.location['history']

        # CORE STRATEGIC PARAMETER: Explicitly link levy to trust capitalization
        self.levy_to_trust_allocation_percentage = 75

    # --- 1. Wholeness ---
    def map_stakeholders(self):
        return {
            "human_actors": {
                "marginalized": ["long_term_residents", "indigenous_farmers", "migrant_laborers"],
                "powerful": ["agribusiness_exporters", "international_esg_investors", "national_water_authority"]
            },
            "non_human_actors": ["river_ecosystem", "regional_aquifer", "native_pollinator_species"]
        }

    def analyze_capital_tradeoffs(self):
        financial_capital = self.location['economy']['export_value_usd']
        natural_capital_degradation = self.bioregion['hydrology']['aquifer_status']
        social_capital_degradation = self.location['sociology']['vulnerabilities']['water_access']

        tradeoff_analysis = (
            f"A critical tradeoff exists: Maximizing Financial Capital (currently ${financial_capital:,} USD in exports) "
            f"directly degrades Natural Capital by causing '{natural_capital_degradation}'. "
            f"This, in turn, erodes Social Capital, evidenced by the fact that '{social_capital_degradation}'"
        )
        return tradeoff_analysis

    def warn_of_cooptation(self, action, framing):
        if framing == "green_capitalism":
            cooptation_narrative = (
                f"Marketing for the action '{action}' could be framed as: "
                f"'Buy our sustainable avocados, grown with 20% less water!' This co-opts the language of sustainability "
                f"while perpetuating the core extractive logic."
            )
            counter_narrative = (
                "A specific counter-narrative must be deployed: 'Our food is the product of a healing valley. "
                "Each purchase pays into a community-governed trust that guarantees water for people and the river first. "
                "This is not just sustainable; it's regenerative, certified by the Valle Seco Bioregional Water Council.'"
            )
            return {"cooptation_risk": cooptation_narrative, "counter_narrative": counter_narrative}
        return {"risk": "No specific framing identified."}

    # --- 2. Nestedness ---
    def analyze_scale_conflicts(self):
        national_policy = self.governance['national_water_code']['framework']
        bioregional_goal = self.bioregion['hydrology']['watershed_health_goals']
        conflict = (
            f"A primary scale conflict exists between the national political scale and the bioregional ecological scale. "
            f"The national policy ('{national_policy}') treats water as an abstract commodity, encouraging hoarding and export. "
            f"This directly undermines the bioregional health goal ('{bioregional_goal}')."
        )
        strategy = (
            "Propose a 'Bioregional Water Council' under a special administrative charter. This council, composed of diverse stakeholders, "
            "would be delegated authority by the national agency to manage water allocations within the valley based on real-time ecosystem "
            "health data, effectively re-embedding the abstract national law within the material reality of the watershed."
        )
        return {"conflict_identification": conflict, "realignment_strategy": strategy}

    # --- 3. Place ---
    def analyze_historical_layers(self):
        injustice = self.historical_context['historical_land_use']
        vulnerability = self.location['sociology']['vulnerabilities']['land_tenure']
        connection = (
            f"The historical injustice of the '{injustice}', which concentrated land in the hands of a few, created the precondition "
            f"for the present-day vulnerability: '{vulnerability}'. This historical pattern enabled the capture of privatized water rights "
            f"by the same landed interests during the 1980s deregulation."
        )
        return connection

    def generate_differential_space_strategy(self):
        return {
            "description": "Strategies to foster use-value over exchange-value, countering the logic of abstract space.",
            "action_1": f"Establish a Community Watershed Trust. This entity will acquire water rights through purchase (primarily capitalized by a dedicated {self.levy_to_trust_allocation_percentage}% of the Watershed Health Levy), donation, and as a condition of new development, holding them in perpetuity for the benefit of the ecosystem and local community.",
            "action_2": "Repurpose abandoned agricultural infrastructure (e.g., packing plants) as a 'Local Production Incubator' for creating value-added products (avocado oil, berry preserves, etc.), building a circular economy that retains value locally rather than exporting it raw."
        }

    # --- 4. Reciprocity ---
    def model_non_monetizable_value(self):
        return {
            "increased_social_cohesion": "Achieved through the stakeholder collaboration required to form and operate the Bioregional Water Council.",
            "knowledge_transfer": "Integration of Traditional Ecological Knowledge (TEK) from indigenous farmers with high-tech monitoring systems, creating a richer, more resilient knowledge base.",
            "revived_cultural_identity": "Restoring the river and local water access reconnects the community to its ancestral relationship with the landscape."
        }

    def guard_against_gentrification(self):
        risk_detection = "The success of the protocol (e.g., a restored river, improved amenities) will likely increase land values, creating a risk of 'green gentrification' where the original residents are priced out."
        mitigation = "Implement a 'first right of refusal' policy for the Community Watershed Trust on all significant land sales. This allows the Trust to acquire land at market rates to develop community-owned housing or lease to local farmers, preventing speculative capture and ensuring local tenure."
        return {"risk": risk_detection, "mitigation_strategy": mitigation}

    def define_reciprocal_actions(self):
        stakeholders = self.map_stakeholders()
        river_ecosystem = stakeholders['non_human_actors'][0]
        reciprocity = {
            "stakeholder": river_ecosystem,
            "human_action": "Guarantee minimum ecological flow levels in all water allocation plans.",
            "ecosystem_benefit": "Restoration of riparian habitat, revival of native fish populations, and enhanced groundwater recharge.",
            "human_benefit": "Increased water security, natural flood mitigation, and restored cultural connection to the river."
        }
        return reciprocity

    # --- 5. Nodal Interventions ---
    def map_planetary_connections(self):
        connection = self.governance['planetary_connections']['supply_chains']
        risk = (
            f"The valley is deeply integrated into global supply chains ('{connection}'). "
            "A specific risk is 'supply chain consolidation,' where dependency on a few large international buyers creates extreme price pressure and suppresses any attempts to diversify crops, locking the valley into an extractive monoculture model."
        )
        return {"connection": "Global Supermarket Supply Chains", "risk": risk}

    def develop_nodal_intervention_strategy(self):
        intervention = (
            f"A 'Watershed Health Levy' on all agricultural exports, calculated based on water footprint and ecological impact. "
            f"A mandatory, non-negotiable allocation of {self.levy_to_trust_allocation_percentage}% of all levy revenues is directed to the "
            "Community Watershed Trust's acquisition fund to re-common water rights."
        )
        greenwashing_risk = "Agribusinesses could pay the levy as a cost of business ('polluter pays' license) and use it for greenwashing PR, while changing no fundamental practices."
        mitigation = (
            "Establish a community-led, multi-tiered 'Agua-Cultura Regenerative Certification'. "
            "The levy is the mandatory baseline (Tier 1). Higher tiers, offering significant market branding advantages and potential price premiums, require demonstrable improvements like agroforestry integration, soil health, and contributions to the Community Watershed Trust. This transforms the levy from a ceiling into a floor."
        )
        return {"intervention": intervention, "greenwashing_risk": greenwashing_risk, "mitigation_strategy": mitigation}

    # --- 6. Pattern Literacy ---
    def generate_place_narrative(self):
        detrimental_pattern = "The 'linear waste stream' of water: pumped from a depleting aquifer, embodied in a fruit, and exported, with the ecological debt left behind."
        life_affirming_pattern = "The 'salmon migration cycle' as a metaphor for a healthy watershed: a cyclical, regenerative system where value flows in, nourishes the system, and what flows out (cleaner water, resilient food) strengthens the whole."
        explanation = (
            "The Agua-Cultura Protocol actively weakens the linear pattern by creating feedback loops (the export levy returns value to the watershed). It strengthens the cyclical pattern by incentivizing farming practices (agroforestry, cover cropping) that mimic natural hydrology, increasing water retention and recharging the aquifer, thereby restoring the valley's 'migration cycle' of water."
        )
        return {"detrimental_abstract_pattern": detrimental_pattern, "life_affirming_local_pattern": life_affirming_pattern, "explanation": explanation}

    def define_counter_patterns(self):
        return {
            "create_closed_loop_system": "Countering the 'linear water export' pattern by mandating high-efficiency irrigation and funding agroforestry projects that build soil organic matter, dramatically increasing the landscape's ability to hold water.",
            "re-common_the_resource": "Countering the 'privatized abstract commodity' pattern of water rights by vesting management in a place-based, stakeholder-governed Bioregional Water Council.",
            "diversify_local_economy": "Countering the 'extractive monoculture' pattern by using the Local Production Incubator to create a diverse web of local enterprises, building economic resilience."
        }

    # --- 7. Levels of Work Framework ---
    def develop_levels_of_work_plan(self):
        regenerate_level_goal = "Build community capacity for self-governance and co-evolution with the watershed."
        return {
            "Operate": {
                "Goal": "Efficiently manage daily water allocations based on Council directives and monitor watershed health indicators.",
                "Activities": ["Run real-time sensor network", "Administer water rights ledger", "Maintain infrastructure."]
            },
            "Maintain": {
                "Goal": "Ensure the long-term functioning of ecological and social infrastructure.",
                "Activities": ["Repair irrigation systems", "Facilitate community meetings", "Update TEK database."]
            },
            "Improve": {
                "Goal": "Continuously enhance the efficiency and effectiveness of the system.",
                "Activities": ["Upgrade monitoring technology", "Refine the 'Agua-Cultura' certification criteria", "Pilot new water-saving cultivation methods."]
            },
            "Regenerate": {
                "Goal": regenerate_level_goal,
                "Activities": [
                    {
                        "Activity": f"Establish and capitalize the Community Watershed Trust, ensuring its power by automatically directing {self.levy_to_trust_allocation_percentage}% of the Watershed Health Levy to its acquisition fund.",
                        "Challenge to Extractive Logic": "This directly challenges the neoliberal logic of water as a privatized, tradable commodity by creating an autonomous, community-controlled financial engine powerful enough to compete with and displace extractive actors on the water market."
                    },
                    {
                        "Activity": "Develop educational programs on watershed literacy for all community members.",
                        "Challenge to Extractive Logic": "This challenges the expert-driven, top-down management model by building distributed intelligence and a shared sense of agency."
                    }
                ],
                "Influence on Other Levels": f"The vision of '{regenerate_level_goal}' provides the guiding context for all other levels. 'Operate' is not just about efficiency, but efficiency in service of regeneration. 'Improve' focuses on enhancements that increase the entire system's capacity to thrive, not just optimizing one part."
            }
        }

    def generate_full_proposal(self):
        self.proposal["1_Wholeness"] = {
            "Stakeholder_Map": self.map_stakeholders(),
            "Capital_Tradeoffs": self.analyze_capital_tradeoffs(),
            "Cooptation_Warning": self.warn_of_cooptation("Introduce water-saving irrigation", "green_capitalism")
        }
        self.proposal["2_Nestedness"] = {
            "Scale_Conflict_Analysis": self.analyze_scale_conflicts()
        }
        self.proposal["3_Place"] = {
            "Historical_Layer_Analysis": self.analyze_historical_layers(),
            "Differential_Space_Strategy": self.generate_differential_space_strategy()
        }
        self.proposal["4_Reciprocity"] = {
            "Non_Monetizable_Value_Model": self.model_non_monetizable_value(),
            "Gentrification_Guard": self.guard_against_gentrification(),
            "Reciprocal_Actions_Example": self.define_reciprocal_actions()
        }
        self.proposal["5_Nodal_Interventions"] = {
            "Planetary_Connections_Map": self.map_planetary_connections(),
            "Intervention_Strategy": self.develop_nodal_intervention_strategy()
        }
        self.proposal["6_Pattern_Literacy"] = {
            "Place_Narrative": self.generate_place_narrative(),
            "Counter_Patterns": self.define_counter_patterns()
        }
        self.proposal["7_Levels_of_Work"] = {
            "Framework_Plan": self.develop_levels_of_work_plan()
        }
        return self.proposal


if __name__ == '__main__':
    protocol_generator = AguaCulturaProtocol(
        location_data=LOCATION_DATA,
        bioregion_data=BIOREGION_DATA,
        governance_data=GOVERNANCE_DATA
    )

    final_proposal = protocol_generator.generate_full_proposal()

    # Output the complete proposal as a JSON string
    print(json.dumps(final_proposal, indent=2))

